function lists(arr) {
    this.arr = arr;
    this.init = init;
    function init() {
        count = 0
        for (i = 0; i < this.arr.length; i++) {
            a = document.createElement('ul');
            a.id = 'list' + count
            document.body.appendChild(a)
            temp = this.arr[i]
            for (j = 0; j < temp.length; j++) {
                tem = temp[j]
                b = document.createElement('li');
                // console.log('<a href=../'+tem.url+'>' +'ðŸŒ¸'+ temp[j].name + '</a>')
                b.innerHTML = '<a href=./'+tem.url+'>' +'ðŸŒ¸'+ temp[j].name + '</a>'
                b.id = 'list' + '-' + i + '-' + j
                b.href = './list' + '-' + i + '-' + j
                // addEventListener
                // b.setAttribute("onclick", 'url(this.id)')
                document.getElementById('list' + count).appendChild(b)
            }
            count += 1
        }
    }
}